import React, { useEffect, useRef, useState } from "react";
import Client from "./Client";
import Editor from "./Editor";
import FileExplorer from "./FileExplorer";
import { initSocket } from "../Socket";
import { ACTIONS } from "../Actions";
import {
  useNavigate,
  useLocation,
  Navigate,
  useParams,
} from "react-router-dom";
import { toast } from "react-hot-toast";

function EditorPage() {
  const [clients, setClients] = useState([]);
  const [files, setFiles] = useState([]);
  const [activeFileId, setActiveFileId] = useState(null);
  const codeRef = useRef(null);

  const Location = useLocation();
  const navigate = useNavigate();
  const { roomId } = useParams();

  const socketRef = useRef(null);

  useEffect(() => {
    const init = async () => {
      socketRef.current = await initSocket();
      socketRef.current.on("connect_error", (err) => handleErrors(err));
      socketRef.current.on("connect_failed", (err) => handleErrors(err));

      const handleErrors = (err) => {
        console.log("Error", err);
        toast.error("Socket connection failed, Try again later");
        navigate("/");
      };

      socketRef.current.emit(ACTIONS.JOIN, {
        roomId,
        username: Location.state?.username,
      });

      // Listen for new clients joining the chatroom
      socketRef.current.on(
        ACTIONS.JOINED,
        ({ clients, username, socketId }) => {
          // this insure that new user connected message do not display to that user itself
          if (username !== Location.state?.username) {
            toast.success(`${username} joined the room.`);
          }
          setClients(clients);
          // also send the code to sync
          socketRef.current.emit(ACTIONS.SYNC_CODE, {
            code: codeRef.current,
            socketId,
            fileId: activeFileId,
          });
        }
      );

      // listening for disconnected
      socketRef.current.on(ACTIONS.DISCONNECTED, ({ socketId, username }) => {
        toast.success(`${username} left the room`);
        setClients((prev) => {
          return prev.filter((client) => client.socketId !== socketId);
        });
      });

      // File sync - receive files when joining
      socketRef.current.on(ACTIONS.FILE_SYNC, ({ files }) => {
        setFiles(files);
        if (files.length > 0 && !activeFileId) {
          setActiveFileId(files[0].id);
        }
      });

      // File management events
      socketRef.current.on(ACTIONS.FILE_CREATE, ({ file }) => {
        setFiles((prev) => {
          // Check if file already exists
          if (prev.find((f) => f.id === file.id)) {
            return prev;
          }
          return [...prev, file];
        });
      });

      socketRef.current.on(ACTIONS.FILE_DELETE, ({ fileId }) => {
        setFiles((prev) => {
          const newFiles = prev.filter((f) => f.id !== fileId);
          // If deleted file was active, switch to first available file
          if (fileId === activeFileId && newFiles.length > 0) {
            setActiveFileId(newFiles[0].id);
          }
          return newFiles;
        });
      });

      socketRef.current.on(ACTIONS.FILE_RENAME, ({ fileId, newName }) => {
        setFiles((prev) =>
          prev.map((f) => (f.id === fileId ? { ...f, name: newName } : f))
        );
      });
    };
    init();

    // cleanup
    return () => {
      socketRef.current && socketRef.current.disconnect();
      socketRef.current.off(ACTIONS.JOINED);
      socketRef.current.off(ACTIONS.DISCONNECTED);
      socketRef.current.off(ACTIONS.FILE_SYNC);
      socketRef.current.off(ACTIONS.FILE_CREATE);
      socketRef.current.off(ACTIONS.FILE_DELETE);
      socketRef.current.off(ACTIONS.FILE_RENAME);
    };
  }, []);

  if (!Location.state) {
    return <Navigate to="/" />;
  }

  const copyRoomId = async () => {
    try {
      await navigator.clipboard.writeText(roomId);
      toast.success(`roomIs is copied`);
    } catch (error) {
      console.log(error);
      toast.error("unable to copy the room Id");
    }
  };

  const leaveRoom = async () => {
    navigate("/");
  };

  // File management handlers
  const handleFileCreate = (fileName) => {
    const newFile = {
      id: `file-${Date.now()}`,
      name: fileName,
      content: "",
    };
    socketRef.current.emit(ACTIONS.FILE_CREATE, {
      roomId,
      file: newFile,
    });
    setActiveFileId(newFile.id);
    toast.success(`${fileName} created`);
  };

  const handleFileDelete = (fileId) => {
    if (files.length <= 1) {
      toast.error("Cannot delete the last file");
      return;
    }
    socketRef.current.emit(ACTIONS.FILE_DELETE, {
      roomId,
      fileId,
    });
    toast.success("File deleted");
  };

  const handleFileRename = (fileId, newName) => {
    socketRef.current.emit(ACTIONS.FILE_RENAME, {
      roomId,
      fileId,
      newName,
    });
    toast.success("File renamed");
  };

  const handleFileSelect = (fileId) => {
    setActiveFileId(fileId);
  };

  const handleFileImport = (fileName, content) => {
    const newFile = {
      id: `file-${Date.now()}`,
      name: fileName,
      content: content,
    };
    socketRef.current.emit(ACTIONS.FILE_CREATE, {
      roomId,
      file: newFile,
    });
    setActiveFileId(newFile.id);
  };

  const handleCodeChange = (code) => {
    codeRef.current = code;
    // Update local file content
    setFiles((prev) =>
      prev.map((f) => (f.id === activeFileId ? { ...f, content: code } : f))
    );
  };

  const activeFile = files.find((f) => f.id === activeFileId);

  return (
    <div className="container-fluid vh-100">
      <div className="row h-100">
        {/* client panel */}
        <div
          className="col-md-2 bg-dark text-light d-flex flex-column h-100"
          style={{ boxShadow: "2px 0px 4px rgba(0, 0, 0, 0.1)" }}
        >
          {/* <img
            src="/images/codewith.png"
            alt="Logo"
            className="img-fluid mx-auto"
            style={{ maxWidth: "150px", marginTop: "-43px" }}
          /> */}
          <hr style={{ marginTop: "-3rem" }} />

          {/* File Explorer */}
          <FileExplorer
            files={files}
            activeFile={activeFileId}
            onFileSelect={handleFileSelect}
            onFileCreate={handleFileCreate}
            onFileDelete={handleFileDelete}
            onFileRename={handleFileRename}
            onFileImport={handleFileImport}
          />

          <hr />

          {/* Client list container */}
          <div className="d-flex flex-column flex-grow-1 overflow-auto">
            <span className="mb-2">Members</span>
            {clients.map((client) => (
              <Client key={client.socketId} username={client.username} />
            ))}
          </div>

          <hr />
          {/* Buttons */}
          <div className="mt-auto ">
            <button className="btn btn-success" onClick={copyRoomId}>
              Copy Room ID
            </button>
            <button
              className="btn btn-danger mt-2 mb-2 px-3 btn-block"
              onClick={leaveRoom}
            >
              Leave Room
            </button>
          </div>
        </div>

        {/* Editor panel */}
        <div className="col-md-10 text-light d-flex flex-column h-100 ">
          {activeFile && (
            <Editor
              socketRef={socketRef}
              roomId={roomId}
              fileId={activeFileId}
              content={activeFile.content}
              onCodeChange={handleCodeChange}
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default EditorPage;
