import React, { useState } from "react";
import { toast } from "react-hot-toast";

function FileExplorer({ files, activeFile, onFileSelect, onFileCreate, onFileDelete, onFileRename, onFileImport }) {
    const [isCreating, setIsCreating] = useState(false);
    const [newFileName, setNewFileName] = useState("");
    const [renamingFile, setRenamingFile] = useState(null);
    const [renameValue, setRenameValue] = useState("");

    const handleCreateFile = () => {
        if (newFileName.trim()) {
            onFileCreate(newFileName.trim());
            setNewFileName("");
            setIsCreating(false);
        }
    };

    const handleRename = (fileId) => {
        if (renameValue.trim()) {
            onFileRename(fileId, renameValue.trim());
            setRenamingFile(null);
            setRenameValue("");
        }
    };

    const handleFileImport = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (event) => {
                const content = event.target.result;
                onFileImport(file.name, content);
                toast.success(`${file.name} imported successfully`);
            };
            reader.onerror = () => {
                toast.error("Failed to read file");
            };
            reader.readAsText(file);
        }
        // Reset input
        e.target.value = "";
    };

    return (
        <div className="file-explorer">
            <div className="d-flex justify-content-between align-items-center mb-2">
                <span className="fw-bold">Files</span>
                <div>
                    <button
                        className="btn btn-sm btn-outline-light me-1"
                        onClick={() => setIsCreating(true)}
                        title="New File"
                    >
                        <i className="fas fa-plus"></i>
                    </button>
                    <label className="btn btn-sm btn-outline-light mb-0" title="Import File">
                        <i className="fas fa-upload"></i>
                        <input
                            type="file"
                            style={{ display: "none" }}
                            onChange={handleFileImport}
                            accept=".js,.jsx,.ts,.tsx,.html,.css,.json,.txt,.md"
                        />
                    </label>
                </div>
            </div>

            {isCreating && (
                <div className="mb-2">
                    <input
                        type="text"
                        className="form-control form-control-sm"
                        placeholder="filename.js"
                        value={newFileName}
                        onChange={(e) => setNewFileName(e.target.value)}
                        onKeyPress={(e) => {
                            if (e.key === "Enter") handleCreateFile();
                            if (e.key === "Escape") setIsCreating(false);
                        }}
                        autoFocus
                    />
                    <div className="mt-1">
                        <button
                            className="btn btn-sm btn-success me-1"
                            onClick={handleCreateFile}
                        >
                            Create
                        </button>
                        <button
                            className="btn btn-sm btn-secondary"
                            onClick={() => {
                                setIsCreating(false);
                                setNewFileName("");
                            }}
                        >
                            Cancel
                        </button>
                    </div>
                </div>
            )}

            <div className="file-list">
                {files.map((file) => (
                    <div
                        key={file.id}
                        className={`file-item ${activeFile === file.id ? "active" : ""}`}
                    >
                        {renamingFile === file.id ? (
                            <div className="d-flex">
                                <input
                                    type="text"
                                    className="form-control form-control-sm"
                                    value={renameValue}
                                    onChange={(e) => setRenameValue(e.target.value)}
                                    onKeyPress={(e) => {
                                        if (e.key === "Enter") handleRename(file.id);
                                        if (e.key === "Escape") setRenamingFile(null);
                                    }}
                                    autoFocus
                                />
                                <button
                                    className="btn btn-sm btn-success ms-1"
                                    onClick={() => handleRename(file.id)}
                                >
                                    âœ“
                                </button>
                            </div>
                        ) : (
                            <div className="d-flex justify-content-between align-items-center">
                                <span
                                    className="file-name"
                                    onClick={() => onFileSelect(file.id)}
                                    style={{ cursor: "pointer", flex: 1 }}
                                >
                                    <i className="fas fa-file-code me-2"></i>
                                    {file.name}
                                </span>
                                <div className="file-actions">
                                    <button
                                        className="btn btn-sm btn-link text-light p-0 me-2"
                                        onClick={() => {
                                            setRenamingFile(file.id);
                                            setRenameValue(file.name);
                                        }}
                                        title="Rename"
                                    >
                                        <i className="fas fa-edit"></i>
                                    </button>
                                    <button
                                        className="btn btn-sm btn-link text-danger p-0"
                                        onClick={() => {
                                            if (window.confirm(`Delete ${file.name}?`)) {
                                                onFileDelete(file.id);
                                            }
                                        }}
                                        title="Delete"
                                    >
                                        <i className="fas fa-trash"></i>
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default FileExplorer;
